mapper.select:
select user_id,account,name,password,gender,phone,address,integral,url,DATE_FORMAT(reg_date,'%Y-%m-%d')regDates,money,user_state, (select name from tb_params where type = 'user_state' and value = user_state) state from tb_user where 1=1
and name like CONCAT('%',#{condition.name},'%')
limit #{curPage},#{pageSize};

select id,user_id,address,class_service,service_count,title,described,phone, DATE_FORMAT(release_time,'%Y-%m-%d')releaseTimes,state,pay_state,(select name from tb_params where type = 'pay_state' and value = t.pay_state ) names ,(select name from tb_user where account = t.user_id) name from tb_demand t where 1=1
and name like CONCAT('%',#{condition.name},'%')
limit #{curPage},#{pageSize};
